        private async Task GenerateImage()
        {
            try
            {
                var pipelineId = await _fusionBrainApi.GetPipelineAsync();  // Получаем идентификатор пайплайна
                var uuid = await _fusionBrainApi.GenerateAsync("природа, гроза, ночь, снизу поля, вдалеке леса и небольшие дома, сверху небо, температура 14 градусов, сильный туман, ветер восточный, скорость ветра 2 метра в секунду", pipelineId); // Генерация картинки
                var files = await _fusionBrainApi.CheckGenerationAsync(uuid); // Проверяем статус генерации

                if (files != null)
                {
                    Debug.WriteLine("Файлы получены:");
                    foreach (var file in files)
                    {
                        Debug.WriteLine(file); // Выводим ссылки на файлы
                        //await SetWallpaper(file);
                    }
                }
                else
                {
                    Debug.WriteLine("Генерация не удалась.");
                }
            }
            catch (Exception ex)
            {
                Debug.WriteLine($"Ошибка генерации изображения: {ex.Message}");
            }
        }

