        private async void LoadWeatherData()
        {
            await OnGetLocation();
            await OnGetWeather();
        }

        async Task OnGetLocation()
        {
            try
            {
                var location = await Geolocation.GetLocationAsync();
                if (location != null)
                {
                    _latitude = location.Latitude;
                    _longitude = location.Longitude;
                    Debug.WriteLine($"Координаты: {_latitude}, {_longitude}");
                }
                else
                {
                    Debug.WriteLine("Не удалось получить местоположение.");
                }
            }
            catch (FeatureNotSupportedException fnsEx)
            {
                Debug.WriteLine("Функция не поддерживается.");
            }
            catch (FeatureNotEnabledException fneEx)
            {
                Debug.WriteLine("Функция не включена.");
            }
            catch (PermissionException pEx)
            {
                Debug.WriteLine("Разрешения недостаточно.");
            }
            catch (Exception ex)
            {
                Debug.WriteLine($"Произошла ошибка: {ex.Message}");
            }
        }

        async Task OnGetWeather()
        {
            try
            {
                // Убедитесь, что у вас есть координаты
                if (_latitude != 0 && _longitude != 0)
                {
                    WeatherData weatherData = await _restService.GetWeatherData(GenerateRequestURL(Constants.MeteoMoodEndpoint));
                    if (weatherData != null)
                    {
                        string cityName = weatherData.City;

                        // Обновляем или добавляем данные
                        if (cityWeatherData.ContainsKey(cityName))
                        {
                            cityWeatherData[cityName] = weatherData; // Обновляем данные для существующего города
                        }
                        else
                        {
                            cityWeatherData.Add(cityName, weatherData); // Добавляем новый город и его данные
                        }

                        // Обновляем ItemsSource после загрузки данных
                        citiesCarousel.ItemsSource = cityWeatherData.Values;
                    }
                    else
                    {
                        Debug.WriteLine("Данные о погоде не получены.");
                    }
                }
                else
                {
                    Debug.WriteLine("Координаты недоступны для запроса погоды.");
                }
            }
            catch (Exception ex)
            {
                Debug.WriteLine($"Ошибка получения данных о погоде: {ex.Message}");
            }
        }

        private bool DetermineIfNight(long sunrise, long sunset)
        {
            var currentTime = DateTime.UtcNow;
            DateTime sunriseTime = DateTimeOffset.FromUnixTimeSeconds(sunrise).UtcDateTime;
            DateTime sunsetTime = DateTimeOffset.FromUnixTimeSeconds(sunset).UtcDateTime;

            return currentTime < sunriseTime || currentTime > sunsetTime;
        }

        string GenerateRequestURL(string endPoint)
        {
            string requestUri = endPoint;
            requestUri += $"?lat={_latitude}";
            requestUri += $"&lon={_longitude}";
            requestUri += "&units=metric";
            requestUri += $"&APPID={Constants.MeteoMoodAPIKey}";
            Debug.WriteLine($"Сгенерированный URL: {requestUri}");
            return requestUri;
        }

        private async void OnAddCityButtonClicked(object sender, EventArgs e)
        {
            if (!AddCityPanel.IsVisible)
            {
                AddCityPanel.IsVisible = true;
                TapBackground.IsVisible = true; // Показать область для нажатий
                await AddCityPanel.FadeTo(1, 250); // Сделать панель видимой с анимацией
                await AddCityPanel.TranslateTo(0, 0, 250, Easing.CubicInOut); // Переместить панель на экран
            }
            else
            {
                await CloseAddCityPanel(); // Закрыть панель
            }
        }

        private async Task CloseAddCityPanel()
        {
            await AddCityPanel.FadeTo(0, 250); // Убрать панель с анимацией
            AddCityPanel.IsVisible = false;
            TapBackground.IsVisible = false; // Скрыть область для нажатий
        }

        private void OnSaveCityButtonClicked(object sender, EventArgs e)
        {
            string cityName = CityEntry.Text;

            if (!string.IsNullOrWhiteSpace(cityName))
            {
                // Сохраните город в вашем списке или выполните необходимые действия
                DisplayAlert("Город добавлен", $"Город {cityName} добавлен успешно.", "OK");

                // Закрыть панель после сохранения
                CityEntry.Text = string.Empty; // Очистка поля ввода
                OnAddCityButtonClicked(null, null); // Закрытие панели
            }
            else
            {
                DisplayAlert("Ошибка", "Введите название города.", "OK");
            }
        }

